// 声明文件只进行类型的声明，不进行具体的实现

type COperators = "plus" | "minus";
// type Calculator = (operator: COperators, nums: number[]) => number;
export interface Calculator {
  (operator: COperators, nums: number[]): number;
  plus: (nums: number[]) => number;
  minus: (nums: number[]) => number;
}

// 为需要使用到的calculator变量声明类型
declare const calculator: Calculator;

export default calculator;
